type Instance struct {
	Net  string // network address, with http
	Port int    // port
}

type Key struct {
	PublicKey  string
	PrivateKey string
	Mode       string
}

type ClientNode struct {
	Instance Instance
	Key      Key
	Balance  int
}

type ServerNode struct {
	Instances []Instance
	Key       Key
}

type Topology struct {
	Servers []ServerNode
	Clients []ClientNode
}

func writeConfig(config []byte, name string) {
	path := fmt.Sprintf("topologies/%s.json", name)
	err := ioutil.WriteFile(path, config, 0644)
	if err != nil {
		panic(err)
	}
}

// prints topology to stdout
func main() {
	writeConfig(localSimple(), "localSimple")
	writeConfig(signTest(), "signTest")
	writeConfig(localSimpleExtended(), "localSimpleExtended")
	writeConfig(localFull(), "localFull")
	writeConfig(dockerSimple(), "dockerSimple")
	writeConfig(awsSimple(), "awsSimple")
	writeConfig(aws(), "aws")
	writeConfig(bls(), "bls")
}

func getClientNetwork(dockerNetwork bool) string {
	if dockerNetwork {
		return "client"
	} else {
		return "localhost"
	}
}

func getServerNetwork(dockerNetwork bool) string {
	if dockerNetwork {
		return "server"
	} else {
		return "localhost"
	}
}

func config(numClients int, numServers int, numServerInstances int, dockerNetwork bool, sameServerPort bool, sigMode int) []byte {
	var serverKeys [][]string
	var clientKeys [][]string
	var readableMode string
	if sigMode == common.ModeEdDSA {
		serverKeys = eddsaServer
		clientKeys = eddsaClient
		readableMode = "eddsa"
	} else if sigMode == common.ModeBLS {
		serverKeys = blsServer
		clientKeys = blsClient
		readableMode = "bls"
	} else {
		panic("invalid mode")
	}

	topo := Topology{}

	counter := 0
	for i := 0; i < numServers; i++ {
		var instances []Instance
		for j := 0; j < numServerInstances; j++ {
			port := 6666
			if !sameServerPort {
				port += counter
			}
			instances = append(instances, Instance{
				Net:  getServerNetwork(dockerNetwork),
				Port: port,
			})
			counter++
		}

		topo.Servers = append(topo.Servers, ServerNode{
			Instances: instances,
			Key: Key{
				PublicKey:  serverKeys[i][0],
				PrivateKey: serverKeys[i][1],
				Mode:       readableMode,
			},
		})
	}

	for i := 0; i < numClients; i++ {
		topo.Clients = append(topo.Clients, ClientNode{
			Instance: Instance{
				Net:  getClientNetwork(dockerNetwork),
				Port: 5555 + i,
			},
			Balance: 100,
			Key: Key{
				PublicKey:  clientKeys[i][0],
				PrivateKey: clientKeys[i][1],
				Mode:       readableMode,
			},
		})
	}

	out, _ := json.MarshalIndent(topo, "", "  ")
	return out
}

// topology optimized for full local testing (includes shards)
func localFull() []byte {
	numClients := 16
	numServers := 1
	numServerInstances := 3

	return config(numClients, numServers, numServerInstances, false, false, common.ModeEdDSA)
}

